- (Club*) strToClub:(NSString*)strClub {
    NSArray *parts = [strClub componentsSeparatedByString:@"|"];
    Club *club = [[Club alloc] init];
    club.club = [parts objectAtIndex:0];
    club.dayofweek = [parts objectAtIndex:1];
    club.level = [parts objectAtIndex:2];
    club.city = [parts objectAtIndex:3];
    club.county = [parts objectAtIndex:4];
    club.caller = [parts objectAtIndex:5];
    club.venue = [parts objectAtIndex:6];
    club.address = [parts objectAtIndex:7];
    club.latitude = [[parts objectAtIndex:8] doubleValue];
    club.longitude = [[parts objectAtIndex:9] doubleValue];
    club.contact = [parts objectAtIndex:10];
    club.web = [parts objectAtIndex:11];
    return club;
}

+ (Data*) sharedClubData {
    
    static Data *sharedClubData = nil;
    
    @synchronized (self)
    {
        if (sharedClubData == nil) {
            sharedClubData = [[Data alloc] init];
        }
        return sharedClubData;
    }
}

- (NSMutableArray*) getClubs {
    return clubs;
}

